import React from "react"
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import PropTypes from "prop-types"
import ReactDOM from 'react-dom';

const useStyles = makeStyles({
  root: {
    width: '70%',
    overflowX: 'auto',
  },
  table: {
    minWidth: '70%',
  },
});

function TableDataView(props) {
  const classes = useStyles();
  return (
    <Paper className={classes.root}>
      <Table className={classes.table} aria-label="simple table">

      <TableHead>
        {props.total_amount_of_month_ary.buy_month}
      </TableHead>

      <TableHead>
        <TableRow bgcolor="#fff0f5">
          <TableCell align="center">カテゴリ名</TableCell>
          <TableCell align="center">商品名</TableCell>
          <TableCell align="center">金額</TableCell>
        </TableRow>
        </TableHead>

        <TableBody>
          {props.buy_items_ary.map(row => (
            <TableRow key={row.name}>
              <TableCell align="center">{row.category_id}</TableCell>
              <TableCell align="center">{row.name}</TableCell>
              <TableCell align="center">{row.price}</TableCell>
            </TableRow>
          ))}

          <TableRow>
            <TableCell colSpan="2" align="center">合計金額</TableCell>
            <TableCell align="center">{props.total_amount_of_month_ary.month_price}</TableCell>
          </TableRow>

        </TableBody>
      </Table>
    </Paper>
  );
}

export default class Buy_Table extends React.Component {
  render() {
    return (
      <React.Fragment>
        <Container maxWidth="lg">
        {this.props.month_hash_key.map((hash_key) =>
          <TableDataView buy_items_ary={this.props.buy_items_ary[hash_key]} total_amount_of_month_ary={this.props.total_amount_of_month_ary[hash_key]} />
        )}
        </Container>
      </React.Fragment>
    );
  }
}
