import React from "react"
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import PropTypes from "prop-types"

const useStyles = makeStyles({
  root: {
    width: '70%',
    overflowX: 'auto',
  },
  table: {
    minWidth: '70%',
  },
});

var rows = []
var month_ary = []
function createData(category_name, name, price) {
  return { category_name:category_name, name:name, price:price };
}

function SimplTable() {
  const classes = useStyles();
  return (
    <Paper className={classes.root}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          201909{month_ary.buy_month }
        </TableHead>
        <TableHead>
          <TableRow bgcolor="#fff0f5">
            <TableCell align="center">カテゴリ名</TableCell>
            <TableCell align="center">商品名</TableCell>
            <TableCell align="center">金額</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => (
            <TableRow key={row.name}>
              <TableCell align="center">{row.category_name}</TableCell>
              <TableCell align="center">{row.name}</TableCell>
              <TableCell align="center">{row.price}</TableCell>
            </TableRow>
          ))}
          <TableRow>
            <TableCell colspan="2" align="center">合計金額</TableCell>
            <TableCell align="center">{month_ary.month_price}</TableCell>
          </TableRow>
        </TableBody>
      </Table>
    </Paper>
  );
}

export default class Buy_Table extends React.Component {
  render() {
    var buy_ary = this.props.buy_ary
    //console.log(buy_ary);
    //console.log(buy_ary['this_month']);
    buy_ary['this_month'].forEach((val) => {
      rows.push(createData(val.category_id, val.name, val.price))
    })
    //console.log(rows)

    var tmp_month = this.props.sum_ary
    console.log(tmp_month['this_month'])

    return (
      <React.Fragment>
        <Container maxWidth="lg">
          <SimplTable />
        </Container>
      </React.Fragment>
    );
  }
}
